/*global module:true*/
/*jslint nomen:true*/
/**
 * @module Utility
 */

var fs = require('fs');
var path = require('path');

(function (context, undefined) {
    'use strict';

    /**
     * Import css files with UTF-8 encoding anywhere in your application.
     * Intended to load static assets directly
     */
    
    function renderCssFile(url,rootPath) {
      var qualPath = __dirname + '/../../' + rootPath + '/' + url;
      var file = fs.readFileSync(qualPath, {encoding:'utf-8'});
      return file;
    }
    
    if ('undefined' !== typeof module && module.exports) {
        module.exports = renderCssFile;
    } else {
        context.truncate = renderCssFile;
    }
}(this));
